import requests
from bs4 import BeautifulSoup
import re


def parse(url):

    # Requestsを使って、webからhtmlを取得
    r = requests.get(url)
    # 要素を抽出
    soup = BeautifulSoup(r.content, 'html.parser')

    # タグの削除
    for s in soup(['script', 'style']):
        s.decompose()

    # 結合
    text = ' '.join(soup.stripped_strings)
    return text


